function isCnpj(a){var b=a.substr(0,12),d=a.substr(12,2),c=0;for(a=0;12>a;a++)c+=b.charAt(11-a)*(2+a%8);if(0==c)return!1;c=11-c%11;9<c&&(c=0);if(d.charAt(0)!=c)return!1;c*=2;for(a=0;12>a;a++)c+=b.charAt(11-a)*(2+(a+1)%8);c=11-c%11;9<c&&(c=0);return d.charAt(1)!=c?!1:!0}
